	}

	.button-login {
		border: 0;
		width: 18rem;
		font-weight: 800;
		color: white;
		height: 4.8rem;
		border-radius: 10px;
		background-color: #00a3ff;
	}

	.or {
		margin: 1rem 0;
	}

	.button-register {
		border: 0;
		width: 18rem;
		font-weight: 800;
		color: white;
		height: 4.8rem;
		border-radius: 10px;
		background-color: #00a757;
	}

	.h1 {
